import './App.css';
import { useState, useEffect} from "react";
import Axios from "axios"

function App() {
  const [listOfPatients, setListOfPatients] = useState ([]);
  const [name, setName] = useState("");
  const [gender, setGender] = useState("");
  const [dob, setDob] = useState("");
  const [height, setHeight] = useState(0);
  const [weight, setWeight] = useState(0);
  const [email, setEmail] = useState("");
  const [medicalHistory, setMedicalHistory] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3001/getPatients").then((response) => {
      setListOfPatients(response.data);
    });
  }, []);

  const createPatient = () => {
    Axios.post("http://localhost:3001/addPatient", {
      name: "",

    }).then((response)=>{
      alert("success, patient added!");
    });
  };

  return (
    <div className="App">
      <div className = "patientsDisplay"> 
        {listOfPatients.map((patient) => {
          return (
            <div>
              <h1>Name: {patient.name}</h1>
              <h1>Gender: {patient.gender}</h1>
              <h1>Date of Birth: {patient.dob}</h1>
              <h1>Height: {patient.height}</h1>
              <h1>Weight: {patient.weight}</h1>
              <h1>Email: {patient.email}</h1>
              <h1>Medical History: {patient.medicalHistory}</h1>
            </div>
          );
        })}
      </div>

      <div>
        <input 
          type = "text" 
          placeholder = "Name" 
          onChange = {(event) => {
            setName(event.target.value);
          }}
        />
        <input 
          type = "text" 
          placeholder = "Gender" 
          onChange = {(event) => {
            setGender(event.target.value);
          }}
        />
        <input 
          type = "text" 
          placeholder = "DOB" 
          onChange = {(event) => {
            setDob(event.target.value);
          }}
        />
        <input 
          type = "number" 
          placeholder = "Height" 
          onChange = {(event) => {
            setHeight(event.target.value);
          }}
        />
        <input 
          type = "number" 
          placeholder = "Weight" 
          onChange = {(event) => {
            setWeight(event.target.value);
          }}
        />
        <input 
          type = "text" 
          placeholder = "Email" 
          onChange = {(event) => {
            setEmail(event.target.value);
          }}
        />
        <input 
          type = "text" 
          placeholder = "Medical History" 
          onChange = {(event) => {
            setMedicalHistory(event.target.value);
          }}
        />
        <button onClick = {createPatient}>Add Patient</button>
      </div>
    </div>
  );
}

export default App;
